import React from "react";
import { useState } from "react";

function Cart({ cart, setCart }) {
  let [price, setPrice] = useState(0);

  return (
    <article>
      {cart.map((menu) => {
        <div className="cart_box" key={menu.id}>
          <div className="cart_img" >
            <img src={menu.img} />
            <p>
              {menu.name}
            </p>
          </div>
          <div>
            <button> + </button>
            <button> - </button>
          </div>
          <div>
            <span>
              {menu.price}
            </span>
            <button> Remove </button>
          </div>
        </div>;
      })}
      <div>
        <span>Total Cost</span>
        <span> Rs - {price}</span>
      </div>
    </article>
  );
}

export default Cart;

