import React, { useState } from "react";

function Cart({ cart, setCart }) {
  let [price, setPrice] = useState(0);

  return (
    <article style={{width:"60%", margin:"auto"}}>
      {cart.map(menu =>
        <div className="cart_box d-flex m-0 align-content-center" key={menu.id}>
          <div className="cart_img d-flex">
            <img src={menu.image} 
            alt={menu.img} 
            width="90px" 
            height="50px"/>
            <p className="tes fw-bold">
              {menu.name}
            </p>
          </div>
          <div>
            <button> + </button>
            <button> - </button>
          </div>
          <div>
            <span>
              {menu.price}
            </span>
            <button> Remove </button>
          </div>
        </div>
      )}
      <div>
        <span>Total Cost</span>
        <span>
          Rs - {price}
        </span>
      </div>
    </article>
  );
}

export default Cart;
