import "./App.css";
import { Routes, Route } from "react-router-dom";
import About from "./Restaurant/Layout/Pages/About";
import Home from "./Restaurant/Layout/Pages/Home";
import Contact from "./Restaurant/Layout/Pages/Contact";
import Menu from "./Restaurant/Layout/Pages/Menu";
import Pagenotfound from "./Restaurant/Layout/Pages/Pagenotfound";
import { useState } from "react";
import Header from "./Restaurant/Layout/Header";
import Footer from "./Restaurant/Layout/Footer";
import Cart from "./Restaurant/Layout/Pages/Cart";

function App() {

  let [cart, setCart] = useState([]);
  let [warning, setWarning] = useState(false);

  const handleClick = menu => {
    let isPresent = false;
    cart.forEach(product => {
      if (menu.id === product.id) isPresent = true;
    });

    if (isPresent) {
      setWarning(true);
      setTimeout(() => {
        setWarning(false);
      }, 2000);

      return;
    }
    setCart([...cart,{...menu, amount:1}]); 
  };

  return (
    <div className="App">
      <Header size={cart.length} />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/menu" element={<Menu handleClick={handleClick} />} />
        <Route path="/cart" element={ <Cart cart={cart} setCart={setCart}/> } />
        <Route path="*" element={<Pagenotfound />} />
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
