import React, { useState } from "react";
import Layout from "../Layout";

function Cart({ cart, setCart }) {
  let [price, setPrice] = useState(0);

  return (
    <Layout>
      <article style={{ width: "60%", margin: "10px auto", marginTop:"80px" }}>
        {cart.map(menu =>
          <div
            className="cart_box d-flex m-4 mt-3 justify-content-between align-items-center"
            key={menu.id}
          >
            <div className="cart_img d-flex w-50 ">
              <img src={menu.image} alt={menu.img} width="90px" height="50px" />
              <p className="tes fw-bold ms-3 align-items-center">
                {menu.name}
              </p>
            </div>
            <div className="btn d-flex justify-content-between">
              <button style={{marginLeft:"5px",width:"20px"}}> + </button>
              <button style={{marginLeft:"5px", width:"20px"}}> - </button>
            </div>
            <div>
              <span>
                {menu.price}
              </span>
              <button> Remove </button>
            </div>
          </div>
        )}
        <div>
          <span>Total Cost</span>
          <span>
            Rs - {price}
          </span>
        </div>
      </article>
    </Layout>
  );
}

export default Cart;
