import React from "react";
import Layout from "../Layout";
import {
  Box,
  Card,
  CardActionArea,
  CardMedia,
  CardContent
} from "@mui/material";
import MenuList from "../../Data/Data";

function Menu({ handleClick }) {
  let [warning, setWarning] = useState(false);
  return (
    <Layout>
      <Box sx={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
        {MenuList.map(menu =>
          <Card sx={{ maxWidth: "390px", m: 3 }}>
            <CardActionArea>
              <CardMedia
                sx={{ minHeight: "400px" }}
                component={"img"}
                src={menu.image}
                alt={menu.name}
              />
              <CardContent>
                <h1>
                  {menu.name}
                </h1>
                <div>
                  {menu.description}
                </div>
                <div>
                  Price : <strong>Rs {menu.price} </strong>
                </div>
                <button onClick={() => handleClick(menu)}>Add To Cart</button>
                {warning &&
                  <div className="warning">
                    Item is already added to your cart
                  </div>}
              </CardContent>
            </CardActionArea>
          </Card>
        )}
      </Box>
    </Layout>
  );
}

export default Menu;
